    private Portata nuovo;

    private void Cancella(Portata daCancellare)
    {
        db.portate.Remove(daCancellare);
        db.SaveChanges();

    }

    private void Aggiungi()
    {
        nuovo = new Portata();
    }

    private void Modifica(Portata daModificare)
    {
        nuovo = daModificare;
    }

    private void Salva()
    {
        if (nuovo.ID == 0)
        {
            db.portate.Add(nuovo);
        }
        else
        {
            db.portate.Update(nuovo);
        }

        db.SaveChanges();
        nuovo = null;
    }


